import { useState } from 'react';

function useUserProvider() {
    const [openModalClient, setOpenModalClient] = useState(false);
    const [openModalClientUpdate, setOpenModalClientUpdate] = useState(false);
    const [isActive, setIsActive] = useState("home")
    const [title, setTitle] = useState("Home")
    const [openModalUpdate, setOpenModalUpdate] = useState(false);
    const [openEdit, setOpenEdit] = useState(false);
    const [currentClient, setCurrentClient] = useState({});
    const [openModalCreateDebt, setOpenModalCreateDebt] = useState(false);

    const [sumDebts, setSumDebts] = useState([]);

    const [countDebts, setCountDebts] = useState([]);
    const [countClients, setCountClients] = useState([]);

    const [listPaidDebts, setListPaidDebts] = useState([]);
    const [listExpiredDebts, setListExpiredDebts] = useState([]);
    const [listPendingDebts, setListPendingDebts] = useState([]);
    const [listDefaulterClient, setListDefaulterClient] = useState([]);
    const [listDebtFreeClient, setListDebtFreeClient] = useState([]);

    return {
        openModalClient, setOpenModalClient,
        openModalClientUpdate, setOpenModalClientUpdate,
        isActive, setIsActive,
        title, setTitle,
        openEdit, setOpenEdit,
        openModalUpdate, setOpenModalUpdate,
        currentClient, setCurrentClient,
        openModalCreateDebt, setOpenModalCreateDebt,
        sumDebts, setSumDebts,

        listPaidDebts, setListPaidDebts,
        listExpiredDebts, setListExpiredDebts,
        listPendingDebts, setListPendingDebts,
        listDefaulterClient, setListDefaulterClient,
        listDebtFreeClient, setListDebtFreeClient
    }
}

export default useUserProvider;
