import './style.css'
import { useState, useEffect } from 'react';
import { toast } from 'react-toastify';

import { listDebtsService } from '../../../services/debtService';
import { getItem } from '../../../utils/Storage';

import ArrowsOrganizeIcon from '../../../assets/arrowclients.svg';
import EditPencilIcon from '../../../assets/edit-debt.svg';
import TrashIcon from '../../../assets/trash-debt.svg';

function DebtsListItems() {
    const [debtsList, setDebtsList] = useState([
      {
          id: 1,
          name: "Sara Silva",
          description: 'oi tudo bem fiquei devendo',
          value: 1000,
          due_date: "2022-05-20T03:00:00.000Z",
          status: "Paga"
      },
      {
          "id": 2,
          "name": "Guilherme Autenticado",
          "description": null,
          "value": 1000,
          "due_date": "2022-05-20T03:00:00.000Z",
          "status": "Vencida"
      },
      {
          "id": 3,
          "name": "Guilherme Autenticado",
          "description": 'não to devendo',
          "value": 1000,
          "due_date": "2022-05-20T03:00:00.000Z",
          "status": "Pendente"
      }
  ]);

    const token = getItem('token');

    useEffect(() => {
      // loadDebts();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    async function loadDebts() {
      try {
          const { data, ok } = await listDebtsService(token);
          if (!ok) {
            return toast.warning(data);
          }

          setDebtsList(data);
      } catch (error) {
          toast.error(error.message);
      }
    }

    return (
        <article className='DebtsListItems'>
            <header className='IndexDebtsList'>
                <div>
                    <img 
                      src={ArrowsOrganizeIcon} 
                      alt="Organizador através do nome do cliente" 
                    />
                    <span>Cliente</span>
                </div>
                <div>
                    <img 
                      src={ArrowsOrganizeIcon} 
                      alt="Icone que organiza o id no formato crescente" 
                    />
                    <span>ID Cob.</span>
                </div>
                <span>Valor</span>
                <span>Data de venc.</span>
                <span>Status</span>
                <span>Descrição</span>
                <span></span>
                <span></span>
            </header>
            {debtsList.map((eachDebt) => (
                <section 
                  className='EachDebt'
                  key={eachDebt.id}
                  >
                    <span className='ClientName'>{eachDebt.name}</span>
                    <span className='ClientID'>{eachDebt.id}</span>
                    <span className='DebtValue'>{eachDebt.value}</span>
                    <span className='DebtDueDate'>{eachDebt.due_date}</span>
                    <span 
                    className={eachDebt.status === 'Vencida' ? 'DebtsStatus Expired' : 
                    eachDebt.status === 'Pendente' ? 'DebtsStatus Pending' : 'DebtsStatus Paid'}
                    >
                      {eachDebt.status}
                    </span>
                    <span className='DebtsDescription'>{eachDebt.description}</span>
                    <button>
                        <img 
                          src={EditPencilIcon} 
                          alt="Icone indicando a possibilidade de editar o cliente" 
                        />
                        <span className='EditWord'>Editar</span>
                    </button>
                    <button>
                        <img 
                          src={TrashIcon} 
                          alt="Icone para apagar o cliente" 
                        />
                        <span className='DeleteWord'>Excluir</span>
                    </button>
                </section>
            ))}
        </article>
    )
}

export default DebtsListItems;
