import React from "react";
import "./style.css";
import Defaulter from "../../../assets/defaulter.svg";
import Expanded from "../../../assets/expanded.svg";

export default function Card({ status, type, data }) {
  return (
    <div className="clients card-container">
      <div className="card-title">
        <div className="card-icon">
          {type === "client" && (
            <img
              src={status === "defaulter" ? Defaulter : Expanded}
              alt="Icon"
              className="icon"
            />
          )}

          {type === "client" && (
            <h1>
              {status === "defaulter"
                ? "Clientes Inadimplentes"
                : "Clientes em dia"}
            </h1>
          )}
        </div>
        <p
          className={
            status === "overdue" || "defaulter"
              ? "client-count red"
              : status === "expected"
                ? "client-count yellow"
                : "client-count green"
          }
        >
          0{data.length}
        </p>
      </div>
      <div className="table-container">
        <div className="table-head">
          <strong className="table-column first-column">
            {type === "client" ? "Clientes" : "Cliente"}
          </strong>
          <strong className="table-column mid-column">
            {type === "client" ? "Data de venc." : "ID da cob."}
          </strong>
          <strong className="table-column last-columns">Valor</strong>
        </div>

        <div className="table-body">
          {data.map((item) => {
            return (
              <div className="table-row" key={item.name}>
                <span className="table-column first-column">{item.name}</span>
                <span className="table-column mid-columns">
                  {type === "client" ? item.date : item.chargeId}
                </span>
                <span className="table-column last-columns">
                  R$ {item.value}
                </span>
              </div>
            );
          })}
        </div>
        <div className="card-bottom">
          <h2>Ver todos</h2>
        </div>
      </div>
    </div>
  );
}
