import "./style.css";
import { useState } from 'react';

import Snackbar from "@mui/material/Snackbar"
import Alert from '@mui/material/Alert';

import useUser from "../../hooks/useUser";

import Sidebar from "../../components/Sidebar";
import Header from "../../components/CmptsHeader/Header";
import MenuList from '../../components/Lists/MenuList';
import ClientsListItems from '../../components/Lists/ClientsListItems';
import ModalCreateClients from "../../components/Modals/ModalCreateClient";
import ModalCreateDebt from '../../components/Modals/ModalCreateDebt';

function Clients() {
  const { setOpenSnackbar, setOpenModalClient, openModalClient, openSnackbar } = useUser();

  const [openModalCreateDebt, setOpenModalCreateDebt] = useState(false);
  const [nameClient, setNameClient] = useState('');

  const handleCloseSnackbar = (event, reason) => {
    if (reason === 'clickaway') {

      return;
    }

    setOpenSnackbar(false);
  };

  return (
    <article className="ContainerClients">
      <Sidebar isActive="clients" />
      <main className="ContentClients">
        <Header title="clients" />
        <article className="Clients">
          <section className="ClientsContainer">
            <MenuList 
            currentPage='clients' 
            setOpenModalClient={setOpenModalClient}
            />
            <ClientsListItems 
            setOpenModalCreateDebt={setOpenModalCreateDebt}
            setNameClient={setNameClient}
            />
          </section>
          {openModalClient &&
            <ModalCreateClients />
          }
          <Snackbar
            open={openSnackbar}
            autoHideDuration={1000}
            onClose={handleCloseSnackbar}
            anchorOrigin={{ vertical: "bottom", horizontal: "right" }}
            >
            <Alert severity="info">Cadastro concluído com sucesso!</Alert>
          </Snackbar>
            <ModalCreateDebt 
              setOpenModalCreateDebt={setOpenModalCreateDebt}
              nameClient={nameClient}
              setNameClient={setNameClient}
            />
        </article>
      </main>
    </article>
  );
}

export default Clients;
