const yup = require('./config');
import { schemaAlert } from '../messages/messageSchema';

const signUpUserStepOne = yup.object().shape({
    nameForm: yup.string().required(),
    emailForm: yup.string().email().required()
});

const signUpUserStepTwo = yup.object().shape({
    passwordForm: yup.string().required(),
    confirmPasswordForm: yup.string().required()
});

const loginUser = yup.object().shape({
    emailForm: yup.string().email().required(),
    passwordForm: yup.string().required()
});

const editUser = yup.object().shape({
    name: yup.string().required(),
    email: yup.string().email().required(),
    password: yup.string().required()
});

export {
    signUpUserStepOne,
    signUpUserStepTwo,
    loginUser,
    editUser
}
