import './style.css'
import { useState, useEffect } from 'react';
import { toast } from 'react-toastify';

import { listClientsService } from '../../../services/clientService';
import { getItem } from '../../../utils/Storage';

import ArrowsOrganizeIcon from '../../../assets/arrowclients.svg';
import NewDebtIcon from '../../../assets/new-debt.svg';

// { setOpenModalCreateDebt, setNameClient }

function ClientsListItems() {
    const [clientsList, setClientsList] = useState([
      {
        cpf: '04478720011',
        name: "Sara Silva",
        email: 'sara@email',
        telefone: '999999999',
        status: "Inadimplente"
      },
      {
        cpf: '34278329971',
        name: "Carla Andrade",
        email: 'carlaaandrade@email',
        telefone: '988888888',
        status: "Em dia"
      },
      {
        cpf: '00008320071',
        name: "Maria Eduarda Furtado",
        email: 'dudaaaaa@email',
        telefone: '9777778888',
        status: "Em dia"
      }
    ]);

    const token = getItem('token');

    useEffect(() => {
      // loadClients();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    async function loadClients() {
      try {
          const { data, ok } = await listClientsService(token);
          if (!ok) {
            return toast.warning(data);
          }

          setClientsList(data);
      } catch (error) {
        return toast.error(error.message);
      }
    }

    function handleModalCreateDebt(clientName) {
      // setOpenModalCreateDebt(true);
      // setNameClient(clientName);
    }

    return (
      <>
        <article className='ClientsListItems'>
            <header className='IndexClientsList'>
                <div>
                    <img 
                      src={ArrowsOrganizeIcon} 
                      alt="Organizador através do nome do cliente" 
                    />
                    <span>Cliente</span>
                </div>
                <span>CPF</span>
                <span>E-mail</span>
                <span>Telefone</span>
                <span>Status</span>
                <span>Criar Cobrança</span>
            </header>
            {clientsList.map((eachClient) => (
                <section 
                  className='EachClient'
                  key={eachClient.cpf}
                  >
                    <span className='ClientName'>{eachClient.name}</span>
                    <span className='ClientCPF'>{eachClient.cpf}</span>
                    <span className='ClientEmail'>{eachClient.email}</span>
                    <span className='ClientPhone'>{eachClient.telefone}</span>
                    <span 
                    className={eachClient.status === 'Em dia' ? 
                    'ClientStatus Ok' : 'ClientStatus Owe'}
                    >
                        {eachClient.status}
                    </span>
                    <button 
                      className='CreateDebt'
                      /* onClick={() => handleModalCreateDebt(eachClient.name)} */
                    >
                        <img 
                          src={NewDebtIcon} 
                          alt="Icone indicando a possibilidade de criar uma cobrança" 
                        />
                        <span className='Debt'>Cobrança</span>
                    </button>
                </section>
            ))} 
        </article>
      </>
    )
}

export default ClientsListItems;
