const BASE_URL = "https://mc-callback.herokuapp.com";
// const BASE_URL = "http://localhost:8000";

async function createDebtService(data, token) {
    const response = await fetch(BASE_URL + '/debt/create', {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
            'Content-Type': 'application/json',
            'token': token
        }
    });

    const responseData = await response.json();

    return { data: responseData, ok: response.ok };
};

async function editDebtService(data, token) {
    const response = await fetch(BASE_URL + '/debt/edit', {
        method: "PUT",
        body: JSON.stringify(data),
        headers: {
            'Content-Type': 'application/json',
            'token': token
        }
    });

    const responseData = await response.json();

    return { data: responseData, ok: response.ok };
};

async function listDebtsService(token) {
    const response = await fetch(BASE_URL + '/debts', {
        method: "GET",
        headers: {
            'token': token
        }
    });

    const responseData = await response.json();

    return { data: responseData, ok: response.ok };
};

async function listDebtsClientService(resource, token) {
    const response = await fetch(BASE_URL + `/debts/clients/${resource}`, {
        method: "GET",
        headers: {
            'token': token
        }
    });

    const responseData = await response.json();

    return { data: responseData, ok: response.ok };
};

export {
    createDebtService,
    editDebtService,
    listDebtsService,
    listDebtsClientService
}
