import "./style.css";
import { useState, useEffect } from "react";
import { toast } from 'react-toastify';
import { getItem } from "../../../utils/Storage";

import CardListDebts from "../CardListDebts";
import CardListClients from "../CardListClients";
import CardSumDebts from "../CardSumDebts";

import { 
  sumDebts, 
  countDebts, 
  listPaidDebts, 
  listExpiredDebts, 
  listPendingDebts 
} from '../../../services/debtService';
import { 
  countClients, 
  listDefaulterClients, 
  listDebtFreeClients
} from '../../../services/clientService';

export default function Resume() {
  const [sumDebts, setSumDebts] = useState([]);

  const [countDebts, setCountDebts] = useState([]);
  const [countClients, setCountClients] = useState([]);

  const [listPaidDebts, setListPaidDebts] = useState([]);
  const [listExpiredDebts, setListExpiredDebts] = useState([]);
  const [listPendingDebts, setListPendingDebts] = useState([]);
  const [listDefaulterClient, setListDefaulterClient] = useState([]);
  const [listDebtFreeClient, setListDebtFreeClient] = useState([]);

  const token = getItem('token');

  useEffect(() => {
    loadHomeData()
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  async function loadHomeData() {
    try {
      const sumD = await sumDebts(token);
      await verifyOk(sumD.ok, sumD.data);
      setSumDebts(sumD.data);

      const countD = await countDebts(token);
      await verifyOk(countD.ok, countD.data);
      setSumDebts(countD.data);

      const countC = await countClients(token);
      await verifyOk(countC.ok, countC.data);
      setSumDebts(countC.data);

      const listPaD = await listPaidDebts(token);
      await verifyOk(listPaD.ok, listPaD.data);
      setSumDebts(listPaD.data);

      const listED = await listExpiredDebts(token);
      await verifyOk(listED.ok, listED.data);
      setSumDebts(listED.data);

      const listPeD = await sumDebts(token);
      await verifyOk(sumD.ok, sumD.data);
      setSumDebts(sumD.data);

      const listDC = await sumDebts(token);
      await verifyOk(sumD.ok, sumD.data);
      setSumDebts(sumD.data);

      const listDFC = await sumDebts(token);
      await verifyOk(sumD.ok, sumD.data);
      setSumDebts(sumD.data);
    } catch (error) {
      return toast.error(error.message);
    }
  } 

  async function verifyOk(ok, data) {
    if (!ok) {
        return toast.warning(data);
      }
  }

  return (
    <main className="ResumeContainer">
      <section className="SumCards">
        <CardSumDebts status="paid" />
        <CardSumDebts status="expired" />
        <CardSumDebts status="pending" />
      </section>
      <section className="DebtsCards">
        <CardListDebts status="paid" data={paidCharges} />
        <CardListDebts status="expired" data={overdueCharges} />
        <CardListDebts status="pending" data={expectedCharges} />
      </section>
      <section className="ClientsCards">
        <CardListClients status="defaulter" data={defaulterClients} />
        <CardListClients status="debt-free" data={payingClients} />
      </section>
    </main>
  );
}
