import "./style.css";
import { useState } from "react";
import { toast } from 'react-toastify';

import { createClientService } from "../../../services/clientService";
import { getItem } from "../../../utils/Storage";

import ImgClose from "../../../assets/close.svg";
import IconClients from "../../../assets/iconclients.svg";

function ModalCreateClient({ openModalClient, setOpenModalClient, setOpenSnackbar }) {
  const [form, setForm] = useState({
    name: '',
    email: '',
    cpf: '',
    phone: '',
    zipcode: '',
    adress: '',
    complement: '',
    district: '',
    city: '',
    state: ''
  });
  const [errorName, setErrorName] = useState('');
  const [errorEmail, setErrorEmail] = useState('');
  const [errorCPF, setErrorCPF] = useState('');
  const [errorPhone, setErrorPhone] = useState('');

  const token = getItem('token');

  function handleFormValue(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault()

    setErrorName('');
    setErrorEmail('');

    if (!form.name) {
      return setErrorName('Este campo deve ser preenchido');
    }
    if (!form.email) {
      return setErrorEmail('Este campo deve ser preenchido');
    }
    if (!form.cpf) {
      return setErrorCPF('Este campo deve ser preenchido');
    }
    if (!form.phone) {
      return setErrorPhone('Este campo deve ser preenchido');
    }

    try {
      const { data, ok } = await createClientService('/client/create', {
          "name": form.name,
          "email": form.email,
          "cpf": form.cpf,
          "phone": form.phone,
          "zipcode": form.zipcode,
          "adress": form.adress,
          "complement": form.complement,
          "district": form.district,
          "city": form.city,
          "state": form.state
        }, token)

      if (!ok) {
        return toast.error(data)
      }

      toast.success('Cliente cadastrado com sucesso');

      setForm({
        name: '',
        email: '',
        cpf: '',
        phone: '',
        zipcode: '',
        adress: '',
        complement: '',
        district: '',
        city: '',
        state: ''
      });

      setOpenSnackbar(true)
      setOpenModalClient(!openModalClient);
    } catch (error) {
      toast.error(error.message);
    }
  }

  return (
    <>
      {openModalClient &&
        <article className="backdrop-client">
          <section className="modal-client">
            <img 
              src={ImgClose}
              alt="Icone com um x sinalizando a possibilidade de fechar o modal"
              className="close-btn-client"
              onClick={() => setOpenModalClient(false)}
            />
            <div className="modal-client-title">
              <img 
                src={IconClients} 
                alt="Clients"
                className="modal-img-clients" 
              />
              <h2>Cadastro do Cliente</h2>
            </div>
            <form onSubmit={handleSubmit}>
              <div className="container-inputs-client">
                <label htmlFor="name">Nome*</label>
                <input type="text"
                  placeholder="Digite o nome"
                  id="name"
                  name="name"
                  value={form.name}
                  onChange={handleFormValue}
                />
                {errorName && <span>{errorName}</span>}
              </div>
              <div className="container-inputs-client">
                <label htmlFor="email">E-mail*</label>
                <input type="text"
                  placeholder="Digite o e-mail"
                  id="email"
                  name="email"
                  value={form.email}
                  onChange={handleFormValue}
                />
                {errorEmail && <span>{errorEmail}</span>}
              </div>
              <div className="div-duo-client">
                <div className="container-inputs-client container-input-duo-client">
                  <label htmlFor="cpf">CPF *</label>
                  <input type="number"
                    placeholder="Digite o CPF"
                    id="cpf"
                    name="cpf"
                    value={form.cpf}
                    onChange={handleFormValue}
                  />
                  {errorCPF && <span>{errorCPF}</span>}
                </div>
                <div className="container-inputs-client container-input-duo-client">
                  <label
                    htmlFor="phone">Telefone *</label>
                  <input type="text"
                    placeholder="Digite o telefone"
                    id="phone"
                    name="phone"
                    value={form.phone}
                    onChange={handleFormValue}
                  />
                  {errorPhone && <span>{errorPhone}</span>}
                </div>
              </div>
              <div className="container-inputs-client">
                <label htmlFor="adress">Endereço</label>
                <input type="text"
                  placeholder="Digite o endereço"
                  id="adress"
                  name="adress"
                  value={form.adress}
                  onChange={handleFormValue}
                />
              </div>
              <div className="container-inputs-client">
                <label htmlFor="complement">Complemento</label>
                <input type="text"
                  placeholder="Digite o seu complemento"
                  id="complement"
                  name="complement"
                  value={form.complement}
                  onChange={handleFormValue}
                />
              </div>
              <div className="div-duo-client">
                <div className="container-inputs-client container-input-duo-client">
                  <label htmlFor="zipcode">CEP</label>
                  <input type="number"
                    placeholder="Digite o CEP"
                    id="zipcode"
                    name="zipcode"
                    value={form.zipcode}
                    onChange={handleFormValue}
                  />
                </div>
                <div className="container-inputs-client container-input-duo-client">
                  <label
                    htmlFor="district">Bairro</label>
                  <input type="text"
                    placeholder="Digite o bairro"
                    id="disctrict"
                    name="district"
                    value={form.district}
                    onChange={handleFormValue}
                  />
                </div>
              </div>
              <div className="div-duo-client">
                <div className="container-inputs-client container-city">
                  <label htmlFor="city">Cidade</label>
                  <input type="text"
                    placeholder="Digite a cidade"
                    id="city"
                    name="city"
                    value={form.city}
                    onChange={handleFormValue}
                  />
                </div>
                <div className="container-inputs-client container-state">
                  <label
                    htmlFor="state">Estado</label>
                  <input type="text"
                    placeholder="Digite o estado"
                    id="state"
                    name="state"
                    value={form.state}
                    onChange={handleFormValue}
                  />
                </div>
              </div>
              <div className="div-btn-client">
                <button type="button" onClick={() => setOpenModalClient(false)} className="btn-cancel">Cancelar</button>
                <button className="btn-confirm">Aplicar</button>
              </div>
            </form>
          </section>
        </article>
      }
    </>
  );
}

export default ModalCreateClient;
