import './style.css'
import { useState, useEffect } from 'react';
import { toast } from 'react-toastify';

import { listDebtsClientService } from '../../../services/debtService';
import { getItem } from '../../../utils/Storage';

import ArrowsOrganizeIcon from '../../../assets/arrowclients.svg';
import EditPencilIcon from '../../../assets/edit-debt.svg';
import TrashIcon from '../../../assets/trash-debt.svg';

const { format } = require('date-fns');

function DebtsByClientListItems({ currentClient, openModalCreateDebt }) {
  const [debtsList, setDebtsList] = useState([
    {
      id: 1,
      name: "Sara Silva",
      description: 'oi tudo bem fiquei devendo',
      value: 1000,
      due_date: "2022-05-20T03:00:00.000Z",
      status: "Paga"
    },
    {
      "id": 2,
      "name": "Guilherme Autenticado",
      "description": null,
      "value": 1000,
      "due_date": "2022-05-20T03:00:00.000Z",
      "status": "Vencida"
    },
    {
      "id": 3,
      "name": "Guilherme Autenticado",
      "description": 'não to devendo',
      "value": 1000,
      "due_date": "2022-05-20T03:00:00.000Z",
      "status": "Pendente"
    }
  ]);

  const token = getItem('token');

  useEffect(() => {
    async function loadDebts() {
      try {
        const { data, ok } = await listDebtsClientService(currentClient.id, token);
        if (!ok) {
          return toast.warning(data);
        }

        setDebtsList(data);
      } catch (error) {
        toast.error(error.message);
      }
    }
    loadDebts();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentClient, openModalCreateDebt])



  return (
    <article className='DebtsListItemsClient'>
      <header className='IndexDebtsListClient'>
        <div>
          <img
            src={ArrowsOrganizeIcon}
            alt="Icone que organiza o id no formato crescente"
          />
          <span>ID Cob.</span>
        </div>
        <div>
          <img
            src={ArrowsOrganizeIcon}
            alt="Icone que organiza o id no formato crescente"
          />
          <span>Data de venc.</span>
        </div>
        <span>Valor</span>
        <span>Status</span>
        <span>Descrição</span>
      </header>
      {
        debtsList.map((eachDebt) => (
          <section
            className='EachDebtClient'
            key={eachDebt.id}
          >
            <span>{eachDebt.id}</span>
            <span>{format(new Date(eachDebt.due_date), "dd/MM/y")}</span>
            <span>
              {new Intl.NumberFormat("pt-BR", {
                style: "currency",
                currency: "BRL"
              }).format(eachDebt.value / 100)}
            </span>
            <span className={
              eachDebt.status === "Vencida" ? "ExpiredClient" : (eachDebt.status === "Pendente" ? "PendingClient" : "PaidClient")
            }>{eachDebt.status}</span>
            <span>{eachDebt.description}</span>
            <button>
              <img
                src={EditPencilIcon}
                alt="Icone indicando a possibilidade de editar o cliente"
              />
              <span className='EditWord'>Editar</span>
            </button>
            <button>
              <img
                src={TrashIcon}
                alt="Icone para apagar o cliente"
              />
              <span className='DeleteWord'>Excluir</span>
            </button>
          </section>
        ))
      }
    </article>
  )
}

export default DebtsByClientListItems;
