import "./style.css";
import React, { useState, useEffect } from "react";

import { userDataService } from "../../../services/userService";

import { getItem } from "../../../utils/Storage"
import { toast } from "react-toastify";

import ProfileOptions from "../ProfileOptions";
import ModalUpdateRegistration from "../../../components/Modals/ModalUpdateRegistration";

import Iconprofile from "../../../assets/iconprofile.svg";
import Arrowprofile from "../../../assets/arrowprofile.svg";

export default function Header({ title }) {
  const [openModalUpdate, setOpenModalUpdate] = useState(false);
  const [openConfirm, setOpenConfirm] = useState(false);
  const [user, setUser] = useState('');

  const token = getItem('token')

  async function loadUser() {
    try {
      const { data, error } = await userDataService('/user', token);
      if (error) {
        toast.error(data)
        return
      }

      setUser(data)
    } catch (error) {
      toast.error(error)
    }
  }

  useEffect(() => {
    loadUser()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [openModalUpdate])

  return (
    <article className="HeaderContainer">
      <section className="HeaderContent">
        <div className={title === "home" ? "HeaderTitle" : "HeaderTitleSmall"}>
          {title === "home" ? (<h1>Resumo das cobranças</h1>) : 
          title === "clients" ? (<span>Clientes</span>) : 
          (<span>Cobranças</span>)}
        </div>
        <div className="ContainerIcon">
          <img 
            src={Iconprofile} 
            alt="Icon com as inicias do usuário" 
            className="IconProfile" 
          />
          <h2>{user.name}</h2>
          <img 
            src={Arrowprofile}
            alt="Seta indicativa para abrir um elemento para baixo"
            className="ArrowProfile"
            onClick={() => setOpenConfirm(!openConfirm)} 
          />
        </div>
      </section>
      <section>
        <ProfileOptions
        open={openConfirm}
        setOpenModalUpdate={setOpenModalUpdate}
        />
        <ModalUpdateRegistration
          open={openModalUpdate}
          handleClose={setOpenModalUpdate}
        />
      </section>
    </article>
  );
}
