import "./style.css";
import { useState, useEffect } from "react";

import { userDataService } from "../../../services/userService";

import { getItem } from "../../../utils/Storage"
import { toast } from "react-toastify";
import useUser from "../../../hooks/useUser"

import ProfileOptions from "../ProfileOptions";
import ModalUpdateRegistration from "../../../components/Modals/ModalUpdateRegistration";

// import Iconprofile from "../../../assets/iconprofile.svg";
import Arrowprofile from "../../../assets/arrowprofile.svg";
import Avatar from '@mui/material/Avatar';

export default function Header() {

  const { title, openModalUpdate, openEdit, setOpenEdit } = useUser()
  const [user, setUser] = useState('');

  const token = getItem('token')

  async function loadUser() {
    try {
      const { data, error } = await userDataService('/user', token);
      if (error) {
        toast.error(data)
        return
      }

      const avatarLetters = data.name.split(' ')[0][0] + data.name.split(' ')[1][0]
      setUser({ ...data, avatar: avatarLetters })
    } catch (error) {
      toast.error(error)
    }
  }

  useEffect(() => {
    loadUser()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [openModalUpdate])

  return (
    <article className="HeaderContainer">
      <section className="HeaderContent">
        <div className={title === "Home" ? "HeaderTitle" : "HeaderTitleSmall"}>
          {title === "Home" ? (<h1>Resumo das cobranças</h1>) :
          title === "Cobranças" ? (<span>Cobranças</span>) :
          title === "Clients" ? (<span>Clientes</span>) :
          (<div className="ContainerDetailClient">
          <span>Clientes</span>
          <span>{'>'}</span>
          <span className="DetailClient">Detalhes do Cliente</span>
          </div>)}
        </div>
        <div className="ContainerIcon">
          <div className="ContainerInfos">
            <Avatar sx={{
              bgcolor: "#DEDEE9",
              color: "#0E8750"
            }} children={user.avatar} />
            <h2>{user.name}</h2>
            <img
              src={Arrowprofile}
              alt="Seta indicativa para abrir um elemento para baixo"
              className="ArrowProfile"
              onClick={() => setOpenEdit(!openEdit)}
            />
            {openEdit &&
              <ProfileOptions />}
          </div>
        </div>
      </section>
      <section>
        {
          openModalUpdate &&
          <ModalUpdateRegistration />
        }
      </section>
    </article>
  );
}
